import React, {
  useState,
  useRef,
  ChangeEvent,
  DragEvent,
  KeyboardEvent,
} from "react";
import {
  XMarkIcon,
  ArrowUpTrayIcon,
  DocumentIcon,
} from "@heroicons/react/24/solid";

type FileInputProps = {
  accept?: string;
  maxSize?: number;
};

const useFileInput = ({ accept = "*", maxSize = Infinity }: FileInputProps) => {
  const [fileName, setFileName] = useState<string | null>(null);
  const [fileSize, setFileSize] = useState<number>(0);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const clearFile = () => {
    setFileName(null);
    setFileSize(0);
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  const validateAndSetFile = (file: File) => {
    if (file.size > maxSize) throw new Error("File is too large");
    if (accept !== "*" && !file.type.match(accept)) {
      throw new Error("File type is not accepted");
    }
    setFileName(file.name);
    setFileSize(file.size);
  };

  return {
    fileName,
    fileSize,
    fileInputRef,
    clearFile,
    validateAndSetFile,
  };
};

const Input_03: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [preview, setPreview] = useState<string | null>(null);
  const [uploadProgress, setUploadProgress] = useState(0);

  const {
    fileName,
    fileSize,
    fileInputRef,
    clearFile,
    validateAndSetFile,
  } = useFileInput({ accept: "image/*", maxSize: 5 * 1024 * 1024 });

  const handleFile = (file: File) => {
    try {
      validateAndSetFile(file);
      simulateUpload(file);
    } catch (err) {
      alert((err as Error).message);
    }
  };

  const simulateUpload = (file: File) => {
    let progress = 0;
    const interval = setInterval(() => {
      progress += 10;
      setUploadProgress(progress);
      if (progress >= 100) {
        clearInterval(interval);
        if (file.type.startsWith("image/")) {
          const reader = new FileReader();
          reader.onloadend = () => {
            setPreview(reader.result as string);
          };
          reader.readAsDataURL(file);
        }
      }
    }, 100);
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);

    const droppedFile = e.dataTransfer.files?.[0];
    if (!droppedFile) return;

    handleFile(droppedFile);
  };

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files?.[0];
    if (!selectedFile) return;
    handleFile(selectedFile);
  };

  const removeFile = () => {
    clearFile();
    setPreview(null);
    setUploadProgress(0);
  };

  return (
    <div className="w-full max-w-md space-y-2">
      <label className="text-sm font-medium text-gray-700 dark:text-gray-300">
        Upload file
      </label>
      <div
        className={`relative group cursor-pointer rounded-lg border-2 border-dashed transition-colors duration-200 ${
          isDragging
            ? "border-indigo-500 bg-indigo-50 dark:bg-indigo-500/10"
            : "border-gray-200 dark:border-gray-800"
        }`}
        onDragOver={(e) => {
          e.preventDefault();
          setIsDragging(true);
        }}
        onDragLeave={() => setIsDragging(false)}
        onDrop={handleDrop}
        onClick={() => fileInputRef.current?.click()}
        onKeyDown={(e: KeyboardEvent<HTMLDivElement>) => {
          if (e.key === "Enter" || e.key === " ") {
            e.preventDefault();
            fileInputRef.current?.click();
          }
        }}
        tabIndex={0}
        aria-label="Upload file"
      >
        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          onChange={handleChange}
          className="hidden"
        />

        <div className="p-8 space-y-4">
          {!fileName ? (
            <div className="flex flex-col items-center gap-2">
              <ArrowUpTrayIcon className="w-8 h-8 text-gray-400 dark:text-gray-500" />
              <p className="text-sm text-gray-600 dark:text-gray-400">
                Drag and drop or click to upload
              </p>
            </div>
          ) : (
            <div className="flex items-center gap-4">
              {preview ? (
                <div className="relative w-16 h-16 rounded-lg overflow-hidden">
                  <img
                    src={preview}
                    alt="Preview"
                    className="w-full h-full object-cover"
                  />
                </div>
              ) : (
                <div className="w-16 h-16 rounded-lg bg-gray-100 dark:bg-gray-800 flex items-center justify-center">
                  <DocumentIcon className="w-8 h-8 text-gray-400" />
                </div>
              )}
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium truncate">
                  {fileName || "No file selected"}
                </p>
                <p className="text-xs text-gray-500">
                  {(fileSize / 1024 / 1024).toFixed(2)} MB
                </p>
                {uploadProgress < 100 && (
                  <div className="mt-2 h-1 w-full bg-gray-100 dark:bg-gray-800 rounded-full overflow-hidden">
                    <div
                      className="h-full bg-indigo-500 transition-all duration-200"
                      style={{ width: `${uploadProgress}%` }}
                    />
                  </div>
                )}
              </div>
              <button
                type="button"
                onClick={(e) => {
                  e.stopPropagation();
                  removeFile();
                }}
                className="p-1 hover:bg-gray-100 dark:hover:bg-gray-800 rounded"
              >
                <XMarkIcon className="w-5 h-5 text-gray-400" />
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Input_03;

import React, { useState, useRef, useEffect, useCallback } from "react";

const OTP_LENGTH = 6;

const Input_06: React.FC = () => {
  const [code, setCode] = useState<string[]>(Array(OTP_LENGTH).fill(""));
  const [activeIndex, setActiveIndex] = useState(0);
  const inputRefs = useRef<(HTMLInputElement | null)[]>([]);
  const [shake, setShake] = useState(false);

  useEffect(() => {
    inputRefs.current = inputRefs.current.slice(0, OTP_LENGTH);
  }, []);

  const handleChange = (index: number, value: string) => {
    if (!/^\d*$/.test(value)) return;

    const newCode = [...code];
    newCode[index] = value.slice(-1);
    setCode(newCode);

    if (value && index < OTP_LENGTH - 1) {
      inputRefs.current[index + 1]?.focus();
      setActiveIndex(index + 1);
    }
  };

  const handleKeyDown = (
    index: number,
    e: React.KeyboardEvent<HTMLInputElement>
  ) => {
    if (e.key === "Backspace") {
      if (code[index] === "") {
        if (index > 0) {
          inputRefs.current[index - 1]?.focus();
          setActiveIndex(index - 1);
        }
      } else {
        const newCode = [...code];
        newCode[index] = "";
        setCode(newCode);
      }
    } else if (e.key === "ArrowLeft" && index > 0) {
      inputRefs.current[index - 1]?.focus();
      setActiveIndex(index - 1);
    } else if (e.key === "ArrowRight" && index < OTP_LENGTH - 1) {
      inputRefs.current[index + 1]?.focus();
      setActiveIndex(index + 1);
    }
  };

  const handlePaste = (e: React.ClipboardEvent) => {
    e.preventDefault();
    const pasted = e.clipboardData.getData("text/plain").slice(0, OTP_LENGTH);
    if (!/^\d+$/.test(pasted)) {
      setShake(true);
      setTimeout(() => setShake(false), 600);
      return;
    }

    const newCode = [...code];
    pasted.split("").forEach((char, i) => {
      newCode[i] = char;
    });

    setCode(newCode);

    const last = Math.min(pasted.length, OTP_LENGTH - 1);
    inputRefs.current[last]?.focus();
    setActiveIndex(last);
  };

  const setInputRef = useCallback(
    (index: number) => (el: HTMLInputElement | null) => {
